class Solution {
public:
    bool wordPattern(string pattern, string s) {
        if (pattern.size() == 0 && s.size() == 0)
            return true;
        if (pattern.size() == 0 || s.size() == 0)
            return false;
        int l=0,r=0;
        vector<string>ans;
        while(r!=s.size())
        {
            if(s[r]==' ')
            {
                ans.push_back(s.substr(l,r-l));
                l=r+1;
            }
            r++;
        }
        ans.push_back(s.substr(l,r-l));
        if (pattern.size() != ans.size()) return false;
        for (int i = 0; i < pattern.size(); i++) {
            if(pattern.find_last_of(pattern[i]) != (find_end(ans.begin(), ans.end(), ans.begin() + i, ans.begin() + i+1) - ans.begin()))
                return false;            
        } 
        return true;
    }
};
