```
function wordPattern(pattern: string, s: string): boolean {
    const patternMap: Map<string, number> = new Map<string, number>();
    const sMap: Map<string, number> = new Map<string, number>(); 

    // Split the string 's' into an array of words
    const splitS = s.split(" ");

    // If the lengths of 'pattern' and 'splitS' are not equal, then patter can not match.
    if (pattern.length !== splitS.length) return false;

    // We give each character in pattern a unique id which is the index
    // and we give also each word in sSplit the same index.

    for (let i = 0; i < pattern.length; i++) {
        if (!patternMap.has(pattern[i])) patternMap.set(pattern[i], i);
        if (!sMap.has(splitS[i])) sMap.set(splitS[i], i);
    }

    // By comparing the indices we can easily 
    // see whether s matches the pattern
    // pattern = "abba", s = "dog cat cat dog"
    // patternMap = {a:0,b:1}
    // sMap = {dog:0,cat:1}
    for (let i = 0; i < pattern.length; i++) {
        if (sMap.get(splitS[i]) !== patternMap.get(pattern[i])) {
            return false;
        }
    }

    return true;
}



