    let obj = {};
    s = s.split(" "); // converting string to array
    if(pattern.length !== s.length) return false;

    for(let i=0; i<pattern.length; i++){
        if(obj[pattern[i]]===undefined) obj[pattern[i]] = s[i];
            // declaring values for every symbol (pattern)
        else if (obj[pattern[i]]!==s[i]) return false
            // if the new value does not matches its last value (symbol's) previous value.
    }
    // the other way round
    let obj2 = {};
    for(let i=0; i<pattern.length; i++){
        if(obj2[s[i]]===undefined) obj2[s[i]] = pattern[i];
            // declaring symbols for every value 
        else if (obj2[s[i]]!==pattern[i]) return false
            // if the new symbol does not matches its symbol (word's) 
    }
    return true
};
