class Solution {
public:
    bool wordPattern(string pattern, string s) {
        unordered_map<char,string> m;// to map every char to corresponding word
        int p=0;// to iterate s 
        int c=0;// to count spaces in s
        set <string> u;// set to check if the string is already mappped with a key check below for better understanding
        for(int i=0;i<s.length();i++)
        {
            if(s[i]==' ')// conting number of words in s (spaces+1)
                c++;
        }
        if(c+1!=pattern.length())// if number of word in s is not equal to number of char in pattern false
            return 0;
        for(int i=0;i<pattern.length();i++)// traverse pattern
        {
            string k="";// string to form for words in s

            while( p<s.length())
            {
                if(s[p]==' ')
                {
                    p++;
                    break;
                }
               k+=s[p];
                p++;
            }
            if(m.find(pattern[i])!=m.end())// if char is already mapped then check if equal to current k(string)
            {
                if(m[pattern[i]]!=k)// if not return 0(false)
                    return 0;
            }
            else if(m.find(pattern[i])==m.end())// if not found the key pattern(char)
            {
                if(u.find(k)==u.end())// if the current string is also not mapped earlier
                {
                    u.insert(k);// insert and map
                  m[pattern[i]]=k;
                }
                else
                  return 0;// if string already present we cannot update map return 
            }       

        }
        return 1;// all cases passed return 1 :)
    }
};
