use std::collections::HashMap;

impl Solution {
    pub fn word_pattern(pattern: String, s: String) -> bool {

        let words : Vec<String> = s.split(' ').map(|x| String::from(x)).collect();
        if words.len() != pattern.len() {
            return false;
        }

        let mut pmap : HashMap<char, Vec<usize>> = HashMap::new();
        let mut wmap : HashMap<String, Vec<usize>> = HashMap::new();

        for (i, c) in pattern.chars().enumerate() {
            let v1 = wmap.entry(words[i].clone()).or_insert(vec![]);
            v1.push(i);
            let v2 = pmap.entry(c).or_insert(vec![]);
            v2.push(i);
        }

        for (c,w) in pattern.chars().zip(words.iter()) {
            if wmap[w] != pmap[&c] {
                return false;
            }
        }

        return true;

    }
}
