class Solution {
public:
    bool wordPattern(string pattern, string str) {
        unordered_map<char, int> dict;
        const int n1 = pattern.size();
        if(n1 == 0) return false;
        string encoding; //On example 1,  "abba" -> "0110"
        for(int i = 0; i < n1; i++){            
            if( !dict.count(pattern[i])) dict[pattern[i]] = i;
            encoding+=dict[pattern[i]];
        }
        stringstream ss(str);
        string word;
        int i = 0;        
        unordered_map<string, int> dict2; // on example 1: {"dog":0, "cat":1}
        while(ss>>word){ // read
            if(i>n1) return false;            
            if(!dict2.count(word)) dict2[word] = i;
            if(dict2[word] != encoding[i]) return false;
            i++;            
        }
        return i == n1;
    }
};
```

