public class Solution {
    public boolean wordPattern(String pattern, String s) {
        // Split the string into a list of words
        String[] words = s.split(" ");

        // Check if the number of words and the number of letters in the pattern are different
        if (words.length != pattern.length()) {
            return false;
        }

        // Create dictionaries to map letters in the pattern to words in the string and vice versa
        Map<Character, String> letterToWord = new HashMap<>();
        Map<String, Character> wordToLetter = new HashMap<>();

        // Iterate through the letters in the pattern
        for (int i = 0; i < pattern.length(); i++) {
            char letter = pattern.charAt(i);
            String word = words[i];

            // Check if the letter has been seen before
            if (letterToWord.containsKey(letter)) {
                // If the letter has been seen before, check if it is mapped to the same word as before
                if (!letterToWord.get(letter).equals(word)) {
                    return false;
                }
            } else {
                // If the letter has not been seen before, add it to the dictionary
                letterToWord.put(letter, word);
            }

            // Check if the word has been seen before
            if (wordToLetter.containsKey(word)) {
                // If the word has been seen before, check if it is mapped to the same letter as before
                if (wordToLetter.get(word) != letter) {
                    return false;
                }
            } else {
                // If the word has not been seen before, add it to the dictionary
                wordToLetter.put(word, letter);
            }
        }

        // If the loop completes without returning false, return true
        return true;
    }
}

