bool wordPattern(string pattern, string str)
{

    // using isstringstream class to extract words from string.
    istringstream iss(str);

    // variable (j) to traverse pattern.
    int j = 0;

    // maps.
    unordered_map<char, string> charToString;
    unordered_map<string, char> stringToChar;

    // extrack all words from string and push it into vector.
    vector<string> words;
    while (iss >> str)
    {
        words.push_back(str);
    }

    // Base Case
    // if total no. of words is not equal to total no. of charater in a pattern string, 
    // then there is no bijection, simply return false.
    if (words.size() != pattern.length())
    {
        return false;
    }

    // traverse the words vector.
    for (int i = 0; i < words.size(); i++)
    {
        // just taking variables of word and char.
        string word = words[i];
        char ch = pattern[j++];

        // find the word and character, check if it is previously appear or not. 
        auto findChar = charToString.find(ch);
        auto findWord = stringToChar.find(word);

        // if character is already appear in the past,
        // this character is mapped to some word.
        if (findChar != charToString.end())
        {
            // extract the mapped word.
            string previousStoredString = charToString[ch];

            // if previously mapped word is not equal to present word, return false.
            if (previousStoredString != word)
            {
                return false;
            }
        }
        else
        {
            // if character is not present, store it in map.
            charToString[ch] = word;
        }

        // if word is already appear in the past,
        // this word is mapped to some character.
        if (findWord != stringToChar.end())
        {
            // extract the mapped character.
            char previousStoredChar = stringToChar[word];

            // if previously mapped character is not equal to present chaacter, return false.
            if (previousStoredChar != ch)
            {
                return false;
            }
        }
        else
        {
            // if word is not present, store it in map.
            stringToChar[word] = ch;
        }
    }

    return true;
}

