class Solution {
public:
    vector<int> inorderTraversal(TreeNode* root) {
        stack<pair<TreeNode*,int>> st;
        vector<int> v;
        if(root == NULL) return {};
        st.push({root,0});
        while(!st.empty()){
            TreeNode* cur = st.top().first;
            int state = st.top().second;
            st.pop();

            if(cur == NULL){
                continue;
            }
            if(state == 0){
                st.push({cur,1});
                st.push({cur->left,0});
            }else if(state == 1){
                v.push_back(cur->val);
                st.push({cur,2});
            }else{
                st.push({cur->right,0});
            }
        }
        return v;
    }

};
```

