#!/bin/bash

target='file.txt';

fields=$(head -n 1 $target);
values=$(tail -n +2 $target | tr "\n" " ");

let count=0;
for i in $fields
do
    tarr[$count]=$i;
    let count++;
done

let s=0;
for value in $values
do
    let index=s%count;
    let s++;
    tarr[$index]="${tarr[index]} $value";
done

i=0;
while [ $i -lt ${#tarr[*]} ]
do
    echo ${tarr[$i]};
    let i++;
done
