class Solution {
public:

    TreeNode* newTreeRight(TreeNode* root, int j){
        if(root==NULL){
            return root;
        }
        TreeNode* newRoot = new TreeNode(root->val + j);
        newRoot->left = newTreeRight(root->left, j);
        newRoot->right = newTreeRight(root->right, j);
        return newRoot;
    }

    vector<TreeNode*> generateTrees(int n) {
        if(n==1){
            return {new TreeNode(1)};
        }
        vector<vector<TreeNode*>> dp(n+1);
        TreeNode* root = new TreeNode(1);
        dp[1].push_back(root);
        for(int i = 2; i<=n; i++){
            for(int j = 1; j<=i; j++){
                int left = j-1;
                int right = i-j;
                if(left==0){
                    for(int k = 0; k<dp[right].size(); k++){
                        root = new TreeNode(j);
                        TreeNode *newRoot = newTreeRight(dp[right][k], j);
                        root->right = newRoot;
                        dp[i].push_back(root);
                    }
                }else if(right==0){
                    for(int k = 0; k<dp[left].size(); k++){
                        root = new TreeNode(j);
                        root->left = dp[left][k];
                        dp[i].push_back(root);
                    }
                }else{
                    for(int k = 0; k<dp[left].size(); k++){
                        for(int l = 0; l<dp[right].size(); l++){
                            root = new TreeNode(j);
                            root->left = dp[left][k];
                            TreeNode *newRoot = newTreeRight(dp[right][l], j);
                            root->right = newRoot;
                            dp[i].push_back(root);
                        }
                    }
                }
            }
        }
        return dp[n];
    }
};
```

