    for (var len = 2; len <= n; len++) {
        for (var start = 1; start <= n - len + 1; start++) {
            const end = start + len - 1;
            for (let mid = start; mid <= end; mid++) {
                const leftSubTrees = mid - 1 - 1 < 0 ? [] : dp[start - 1][mid - 1 - 1];
