        trees = []
        for i in range(len(nodes)):
            for leftSubTree, rightSubTree in product(self.BST(nodes[:i]), self.BST(nodes[i+1:])):
                root = TreeNode(nodes[i])
                root.left, root.right = leftSubTree, rightSubTree
                trees.append(root)

