/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */

class Solution {
public:
    vector<vector<vector<TreeNode*>>> dp; // 3D vector to store computed values for dynamic programming

    // Function to generate all possible unique binary search trees given a range [s, e]
    vector<TreeNode*> BSTree(int s, int e) {
        // Base cases: If the range is invalid or contains a single element, return appropriate values.
        if (s > e) return {NULL}; // A single element in the range [s, e] cannot form a tree, so return NULL.
        if (s == e) return {new TreeNode(s)}; // A single element in the range is a valid tree by itself.

        if (dp[s][e].size() > 0) return dp[s][e]; // If the value has already been computed, return it from the dp array.

        vector<TreeNode*> ans; // Vector to store all possible BSTs in the range [s, e].

        // Loop through each possible root node value (j) in the range [s, e]
        for (int j = s; j <= e; j++) {
            // Recursively generate all possible left subtrees (Left) from range [s, j-1]
            vector<TreeNode*> Left = BSTree(s, j - 1);

            // Recursively generate all possible right subtrees (Right) from range [j+1, e]
            vector<TreeNode*> Right = BSTree(j + 1, e);

            // Combine all possible combinations of left and right subtrees with the current root value (j)
            for (TreeNode* l : Left) {
                for (TreeNode* r : Right) {
                    TreeNode* root = new TreeNode(j, l, r); // Create a new tree with root value (j), left subtree (l), and right subtree (r).
                    ans.push_back(root); // Add the current tree to the result vector.
                }
            }
        }

        return dp[s][e] = ans; // Store the computed value in the dp array and return the result.
    }

    vector<TreeNode*> generateTrees(int n) {
        dp.assign(n + 1, vector(n + 1, vector<TreeNode*>())); // Initialize the dp array with dimensions (n+1) x (n+1) x unknown.
        return BSTree(1, n); // Call the helper function with the range [1, n] to generate all possible BSTs from 1 to n.
    }
};

```

