    public List<TreeNode> generateTrees(int n) {
        if (n == 0) return new ArrayList<>();
        if (n == 1) return new ArrayList<>(Arrays.asList(new TreeNode(n)));

        // Map start + " " + end to list of placement.
        Map<String, List<TreeNode>> memo = new HashMap<>(); 

        return place(1, n, memo);
    }

    private List<TreeNode> place(int start, int end, Map<String, List<TreeNode>> memo) {
        List<TreeNode> placement = new ArrayList<>(); // List of root of each placement.
        // Base cases.
        if (start > end) {
            placement.add(null);
            return placement;
        }
        if (start == end) {
            placement.add(new TreeNode(start));
            return placement;
        }

        String memoKey = start + " " + end;
        if (memo.containsKey(memoKey)) return memo.get(memoKey);

        for (int x = start; x <= end; x++) {
            List<TreeNode> leftPlacement = place(start, x - 1, memo);
            List<TreeNode> rightPlacement = place(x + 1, end, memo);
            TreeNode root;
            for (TreeNode leftNode: leftPlacement) {
                for (TreeNode rightNode: rightPlacement) {
                    root = new TreeNode(x);
                    root.left = leftNode;
                    root.right = rightNode;
                    placement.add(root);
                }
            }
        }

        memo.put(memoKey, placement);
        return placement;
    }
