> Scala
```
import scala.collection.mutable.{ListBuffer, HashMap}

object Solution {
  def generateTrees(n: Int): List[TreeNode] = {
    if (n < 1) List()
    else generate(1, n, new HashMap[String, List[TreeNode]]())
  }

  def generate(start: Int, end: Int, memo: HashMap[String, List[TreeNode]]): List[TreeNode] = {
    val memoKey = start + " " + end

    if (!(memo contains memoKey)) {
      val rootList = new ListBuffer[TreeNode]()

      if (start > end) rootList += null
      else if (start == end) rootList += new TreeNode(start)
      else {
        for (x <- start to end) {
          val leftList = generate(start, x - 1, memo)
          val rightList = generate(x + 1, end, memo)
          for (leftNode <- leftList) {
            for (rightNode <- rightList) {
              val root = new TreeNode(x)
              root.left = leftNode
              root.right = rightNode
              rootList += root
            }
          }
        }
      }

      memo += memoKey -> rootList.toList
    }

    memo.get(memoKey).get
  }
}
