        if(n == 0) return {};
        vector<vector<TreeNode*>> dp(n+1);
        dp[0].push_back(nullptr);
        dp[1].push_back(new TreeNode(1));
        for(int i=2;i<=n;i++) {
            for(int j=1;j<=i;j++) {
                int leftSize = j-1;
                int rightSize = i-j;
                for(TreeNode* l: dp[leftSize]) {
                    for(TreeNode* r: dp[rightSize]) {
                        TreeNode* node = new TreeNode(j);
                        node->left = cloneAndAddX(l,0);
                        node->right = cloneAndAddX(r,j);
                        dp[i].push_back(node);
                    }
                }
            }
        }
        return dp[n];
