func generateTrees(n int) []*TreeNode {
	if n <= 0 {
		return []*TreeNode{}
	}
	return helper(1, n)
}

func helper(min, max int) []*TreeNode {
	retSlice := []*TreeNode{}
	for i := min; i <= max; i++ {
		left := helper(min, i-1)
		right := helper(i+1, max)
		for _, l := range left {
			for _, r := range right {
				retSlice = append(retSlice, &TreeNode{
					Val:   i,
					Left:  l,
					Right: r,
				})
			}
		}
	}
	// if empty, we need a nil node
	if len(retSlice) == 0 {
		retSlice = append(retSlice, nil)
	}
	return retSlice
}
