                for (auto &right: rightL) {
                    TreeNode *head = new TreeNode(i);
                    head->left = left;
                    head->right = right;
                    ans.push_back(head);
                }
            }
        }
        return ans;
    }

    vector<TreeNode*> generateTrees(int n) {
        if (n < 1) return {};
        return gen(1,n);
    }
};
```

