Tabulation:

    class Solution {

        using Trees = vector<TreeNode*>;

    public:
        vector<TreeNode*> generateTrees(int n) {
            if (n == 0)
            {
                return vector<TreeNode*>({NULL});
            }

            // the dp table: [i][j] -> all unique BSTs with nodes from i..j inclusive
            vector<vector<Trees>> dp(n + 1,
                                     vector<vector<TreeNode*>>(n + 1, Trees()));

            // initialize the dp table: [i][i] -> a single root node with value i, for all i in [1,n] inclusive
            for (int i = 1; i <= n; ++i)
            {
                dp[i][i].push_back(new TreeNode(i));
            }

            // the progressively build the table (bottom-up tabulation)
            for (int k = 1; k < n; ++k)
            {
                for (int i = 1; i <= n - k; ++i)
                {
                    int j = i + k;
                    Trees &unique_trees = dp[i][j]; assert (unique_trees.empty());  // this is what we're building right now
                    for (int root = i; root <= j; ++root)
                    {
                        int end_left = root - 1;
                        int start_right = root + 1;
                        Trees all_left;
                        if (end_left < i)
                        {
                            all_left.push_back(NULL);
                        }
                        else
                        {
                            all_left = dp[i][end_left];
                        }
                        Trees all_right;
                        if (j < start_right)
                        {
                            all_right.push_back(NULL);
                        }
                        else
                        {
                            all_right = dp[start_right][j];
                        }
                        for (TreeNode *left : all_left)
                        {
                            for (TreeNode *right : all_right)
                            {
                                TreeNode *root_node = new TreeNode(root);
                                root_node->left = left;
                                root_node->right = right;
                                unique_trees.push_back(root_node);
                            }
                        }
                    }
                }
            }

            return dp[1][n];
        }
    };


Memoization:

    class Solution {
    private:

        using cache = map<pair<int, int>, vector<TreeNode*>>;   // {i,j} -> all unique BSTs with nodes from i..j inclusive

        vector<TreeNode*> generate_trees_dp(int start, int end, cache &dp)
        {
            vector<TreeNode*> result;

            if (end < start)
            {
                result.push_back(NULL);
                return result;
            }

            auto it = dp.find({start, end});
            if (it != dp.end())
            {
                return it->second;
            }

            for (int i = start; i <= end; ++i)
            {
                vector<TreeNode*> all_left = generate_trees_dp(start, i - 1, dp);
                vector<TreeNode*> all_right = generate_trees_dp(i + 1, end, dp);
                for (TreeNode *left_tree : all_left)
                {
                    for (TreeNode *right_tree : all_right)
                    {
                        TreeNode *root = new TreeNode(i);
                        root->left = left_tree;
                        root->right = right_tree;
                        result.push_back(root);
                    }
                }
            }

            dp[{start, end}] = result;
            return result;
        }

    public:
        vector<TreeNode*> generateTrees(int n) {
            cache dp;
            // initialize the dp cache: {i,i} -> a single root node with value i, for all i in [1,n] inclusive
            for (int i = 1; i <= n; ++i)
            {
                dp[{i,i}] = { new TreeNode(i), };
            }

            return generate_trees_dp(1, n, dp);
        }
    };

