class MedianFinder:
    def __init__(self):
        """
        initialize your data structure here.
        """
        self.store = []

    def addNum(self, num: int) -> None:
        self.store.append(num)

    def findMedian(self) -> float:
        self.store.sort()

        n = len(self.store)-1
        return self.store[n//2] if n % 2 == 0 else (self.store[n//2] + self.store[n//2 + 1])/2.0

# insertion sort -> bisect using binary search O(logN)
# insert -> O(N)
# Time O(N) + O(logN) = O(N)
from bisect import bisect_left
class MedianFinder:

    def __init__(self):
        """
        initialize your data structure here.
        """
        self.store = []

    def addNum(self, num: int) -> None:
        if not self.store:
            self.store.append(num)
        else:
            idx = bisect_left(self.store, num)
            self.store.insert(idx, num)

    def findMedian(self) -> float:        
        n = len(self.store)-1
        return self.store[n//2] if n % 2 == 0 else (self.store[n//2] + self.store[n//2 + 1])/2.0

# Time O(logN)
from heapq import heappush, heappop
class MedianFinder:

    def __init__(self):
        """
        initialize your data structure here.
        """
        # holds the smaller half
        self.max = []
        # holds the larger half
        self.min = []


    def addNum(self, num: int) -> None:
        heappush(self.max, -num)
        heappush(self.min, -heappop(self.max))
        if len(self.min) > len(self.max):
            heappush(self.max, -heappop(self.min))


    def findMedian(self) -> float:
        if len(self.max) == len(self.min):
            return (self.min[0] - self.max[0])/2.0
        else:
            return -self.max[0]

