* ***Using Heap***

* ***Time Complexity :- O(logN) for both add and median function***

* ***Space Complexity :- O(N)***

```
class MedianFinder {
public:

    // smaller will contain half smaller elements

    priority_queue<int> smaller;

    // larger will contain half larger elements

    priority_queue<int, vector<int>, greater<int>> larger;

    MedianFinder() {

    }

    void addNum(int num) {

        // push the num into smaller

        smaller.push(num);

        // if largest element of smaller is greater than the smallest element of larger, then push the top of smaller into larger

        if(!smaller.empty() && !larger.empty() && smaller.top() > larger.top())
        {
            int val = smaller.top();

            smaller.pop();

            larger.push(val);
        }

        // if the size of smaller is greater than larger by 2 then push the top of smaller into larger

        if(smaller.size() > larger.size() + 1)
        {
            int val = smaller.top();

            smaller.pop();

            larger.push(val);
        }

        // if the size of larger is greater than smaller by 2 then push the top of larger into smaller

        else if(larger.size() > smaller.size() + 1)
        {
            int val = larger.top();

            larger.pop();

            smaller.push(val);
        }
    }

    double findMedian() {

        if(smaller.size() == larger.size())
        {
            return (double(smaller.top()) + double(larger.top())) / 2;
        }

        else if(smaller.size() == larger.size() + 1)
        {
            return double(smaller.top());
        }

        else
        {
            return double(larger.top());
        }
    }
};
```

