        arr = new int[101];
        size = 0;
        lo = new PriorityQueue<Integer>();
        hi = new PriorityQueue<Integer>();
    }

    public void addNum(int num) {
        if(num < 0) lo.add(num);
        else if(num > 100) hi.add(num);
        else arr[num]++;
        size++;
    };

    public double findMedian() {
        int median = size / 2, index;
        PriorityQueue<Integer> temp;
        // If total size is odd number
        if(size % 2 != 0) {
            if(median < lo.size()) {
                // The median is in lo
				// Todo:
            }
            else if(median >= size - hi.size()) {
                // The median is in hi
				// Todo:
            } 
            else {
                // The median is in arr
				// Todo:
            }
        } // If total size is even number
        else {
            int a, b;
            if(median < lo.size()) {
                temp = new PrirotyQueue<>(lo);
                index = 0;
                while(index < median) {
                    if(index == median - 1) {
                        a = temp.poll();
                        b = temp.poll();
                        return (a + b) / 2.0;
                    } else {
                        temp.poll();    
                        index++;
                    }
                }
            } else if(median == lo.size()) {
                int last = 0;
                temp = new PrirotyQueue<>(lo);
                while(!temp.isEmpty()) last = temp.poll();
                for(int i = 0; i < 101; i++)
                    if(arr[i] != 0) 
                        return (i + last) / 2.0;
            } else if(median > size - hi.size()) {
                temp = new PrirotyQueue<>(hi);
                index = size - hi.size();
