public class MedianFinder {
    private Tree tree ;
    /** initialize your data structure here. */
    public MedianFinder() {
        tree = new Tree();
    }

    public void addNum(int num) {
        tree.add(num);
    }

    public double findMedian() {
        int size = tree.size();
        int half = size >> 1;
        if(size %2 == 1){
            return tree.getIth(1 + half);
        }
        else{
            return (tree.getIth(half) + tree.getIth(half +1)) / (double)2;
