public class MedianFinder {
    PriorityQueue<int, int> max = new(Comparer<int>.Create((x, y) => y.CompareTo(x)));
    PriorityQueue<int, int> min = new();

    public void AddNum(int num) {
        max.Enqueue(num, num);
        min.Enqueue(max.Peek(), max.Dequeue());
        if (max.Count < min.Count)
            max.Enqueue(min.Peek(), min.Dequeue());
    }

    public double FindMedian() => min.Count != max.Count
        ? max.Peek() : (min.Peek() + max.Peek()) / 2.0;
}
```

