type MedianFinder struct {
    minH minHeap
    maxH maxHeap
}

func Constructor() MedianFinder {
    var minH minHeap
    var maxH maxHeap

    return MedianFinder {
        minH: minH,
        maxH: maxH,
    }
}

func (this *MedianFinder) AddNum(num int)  {
    if len(this.maxH) == 0 || num <= this.maxH[0] {
        this.maxH.add(num)
    } else {
        this.minH.add(num)
    }

    if len(this.maxH) > len(this.minH)+1 {
        this.minH.add(this.maxH.pop())
    } else if len(this.maxH) < len(this.minH) {
        this.maxH.add(this.minH.pop())
    }
}

func (this *MedianFinder) FindMedian() float64 {
    if len(this.minH) == len(this.maxH) {
        return (float64(this.minH[0]) + float64(this.maxH[0]))/float64(2)
    }

    return float64(this.maxH[0])
}

type minHeap []int

type maxHeap []int

func (h *maxHeap) pop() int {
    poppedItem := (*h)[0]

    (*h)[0] = (*h)[len(*h)-1]
    (*h) = (*h)[:len(*h)-1]
    h.heapDown(0)

    return poppedItem
}

func (h *minHeap) pop() int {
    poppedItem := (*h)[0]

    (*h)[0] = (*h)[len(*h)-1]
    (*h) = (*h)[:len(*h)-1]
    h.heapDown(0)

    return poppedItem
}

func (h *minHeap) add(num int) {
    *h = append(*h, num)
    h.heapUp(len(*h)-1)
}

func (h *maxHeap) add(num int) {
    *h = append(*h, num)
    h.heapUp(len(*h)-1)
}

func (h *minHeap) heapDown(p int) {
    l, r := 2*p+1, 2*p+2
    smaller := p

    if l < len(*h) && (*h)[l] < (*h)[smaller] {
        smaller = l
    }

    if r < len(*h) && (*h)[r] < (*h)[smaller] {
        smaller = r
    }

    if smaller != p {
        (*h)[smaller], (*h)[p] = (*h)[p], (*h)[smaller]
        h.heapDown(smaller)
    }
}

func (h *minHeap) heapUp(p int) {
    parent := (p-1)/2

    if parent >= 0 && (*h)[p] < (*h)[parent] {
        (*h)[p], (*h)[parent] = (*h)[parent], (*h)[p]
        h.heapUp(parent)
    }
}

func (h *maxHeap) heapDown(p int) {
    l, r := 2*p+1, 2*p+2
    bigger := p 

    if l < len(*h) && (*h)[l] > (*h)[bigger] {
        bigger = l
    }

    if r < len(*h)&& (*h)[r] > (*h)[bigger] {
        bigger = r
    }

    if bigger != p {
        (*h)[bigger], (*h)[p] = (*h)[p], (*h)[bigger]
        h.heapDown(bigger)
    }
}

func (h *maxHeap) heapUp(p int) {
    parent := (p-1)/2

    if parent >= 0 && (*h)[p] > (*h)[parent] {
        (*h)[p], (*h)[parent] = (*h)[parent], (*h)[p]
        h.heapUp(parent)
    }
}
