    if (this.size() === 0) return 0;
    let isSizeEven = this.size() % 2 === 0;
    let mid = isSizeEven ? (this.size() / 2) : ((this.size() - 1) / 2);
    return isSizeEven ? (this.nums[mid] + this.nums[mid - 1]) / 2 : this.nums[mid];
