// Time Complexity:
// addNum: O(1)
// findMedian: O(m),  m is the length of buckets, in this case, it's 101 
class MedianFinder {

    int[] buckets;
    int len;

    /** initialize your data structure here. */
    public MedianFinder() {
        buckets = new int[101];
        len = 0;
    }

    public void addNum(int num) {
        len ++;
        buckets[num] ++;
    }

    public double findMedian() {
        double half = (double) len / 2;
        int sum = 0;
        int preSum = 0;
        if(len % 2 == 0){
            for(int i = 0; i < buckets.length; i ++){
                sum += buckets[i];
                if(sum > half && preSum < half){
                    return (double) i;
                }
