     */
    function lengthOfLongestSubstring($s) 
    {
        if (strlen($s) === 0) return 0;
        if (strlen($s) === 1) return 1;

        $chars = str_split($s);

        $i = $j = $max = 0;
        $seen = [];

        while ($i < count($chars))
        {
            $c = $chars[$i];

            while (array_key_exists($c, $seen))
            {
                unset($seen[$chars[$j]]);
        		$j++;
            }

            $seen[$chars[$i]] = true;

            $max = max($i - $j + 1, $max);
            $i++;
        }
        return $max;
    }
