    int lengthOfLongestSubstring(string s) {
        // Create a hash-map slide window.
        unordered_map<char,int> window;
        // Initialize the slide window. 
        int left = 0, right = 0;
        // Record the result.
        int res = 0;
        while (right < s.size()) {
            char c = s[right];
            // Include c in the window.
            window[c]++;
            //check if c is already in the window
            while (window[c] > 1) {
                // Shrink the window from left side, to the point that there is no more repeating characters. 
                char d = s[left];
                left ++;
                window[d] --;
            }
            // Expand the window from right side. 
            right ++;
            // Make sure current res is the longest result. 
            res = max(res, right - left);
        }
        return res;
    }
