                if(s.size()<1) return s.size();
                int l=0, r=0, len=1;
                unordered_set<char> window;
                while(r<s.size()){
                    if(window.find(s[r])==window.end()){
                        window.insert(s[r++]);
                        len=max(len, r-l);
                    }else{
                        while(s[l]!=s[r]) window.erase(s[l++]);
                        l++;
                        r++;
                    }
