            else{
                while(s.charAt(left)!=s.charAt(right)){
                    hs.remove(s.charAt(left));
                    left++;
                }
                hs.remove(s.charAt(left));
                left++;
                hs.add(s.charAt(right));
            }
        }
        return curr_max;    
