class Solution {
public:
    int lengthOfLongestSubstring(string input) {
        unordered_map<char,int> hash;
        int i=0, count=0,maxlen=-100,start=0;
        hash[input[0]] = 0;
        if(input.size() == 0)
            return 0;
        for(i=1;i<input.size();i++)
        {
            // cout<<"i is: "<<i<<" count is: "<<count<<" window String is: ";
            // for(int j=start; j<=i;j++)
            //     cout<<input[j];
            // cout<<endl;
            if(hash.find(input[i]) == hash.end())
                hash[input[i]] = i;
            else
            {
                count = i-start;
                // cout<<"\tCount after Update: "<<count<<endl;
                if(count > maxlen)
                    maxlen = count;
                if(hash[input[i]] >= start)
                    start = hash[input[i]]+1;
                hash[input[i]] = i;
            }
        }
        count = i - start;
        if(count > maxlen)
            maxlen = count;
        return maxlen;
    }
};

