bool isSameTree(TreeNode* p, TreeNode* q) {
    queue<pair<TreeNode*, TreeNode*>> myQueue;
    myQueue.push(pair<TreeNode*, TreeNode*>(p, q));
    while (!myQueue.empty()) {
        p = myQueue.front().first;
        q = myQueue.front().second;
        if(!p ^ !q || (p && q && p->val != q->val))
