/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public void preOrder(TreeNode p,ArrayList list){
        if (p==null) return;
        list.add(p.val);
        preOrder(p.left,list);
        preOrder(p.right,list);
    }
    public boolean isSameTree(TreeNode p, TreeNode q) {
        ArrayList list_p=new ArrayList<>();
        ArrayList list_q=new ArrayList<>(); 
        preOrder(p,list_p);
        preOrder(q,list_q);
        return list_p==list_q;        
    }
}

