**Method 1: DFS**
Time Complexity : O(n x m)
Space Complexity: O(n x m)

```
class Solution {
public:
    int DR[4]={1, 0, -1, 0};
    int DC[4]={0, -1, 0, 1};

    bool valid_index(int i, int j, vector<vector<char>>& grid) {
        if(i<0 || j<0 || i>=grid.size() || j>=grid[0].size())
            return false;
        return true;
    }

    void dfs(int i, int j, vector<vector<char>>& grid) {
        grid[i][j]='0';
        for(int k=0; k<4; k++) {
            int ci=DR[k]+i;
            int cj=DC[k]+j;
            if(!valid_index(ci, cj, grid))
                continue;
            if(grid[ci][cj]=='1')
                dfs(ci, cj, grid);
        }
    }

    int numIslands(vector<vector<char>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int no_of_islands=0;
        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                if(grid[i][j]=='1') {
                    no_of_islands++;
                    dfs(i, j, grid);
                }
            }
        }
        return no_of_islands;
    }
};
```

**Method 2: BFS**
Time Complexity : O(n x m)
Space Complexity: O(n x m)

```
class Solution {
public:
    int DR[4]={1, 0, -1, 0};
    int DC[4]={0, -1, 0, 1};

    bool valid_index(int i, int j, vector<vector<char>>& grid) {
        if(i<0 || j<0 || i>=grid.size() || j>=grid[0].size())
            return false;
        return true;
    }

    void bfs(int i, int j, vector<vector<char>>& grid) {
        grid[i][j]='0';
        queue<pair<int, int>> q;
        q.push({i, j});
        while(!q.empty()) {
            int i=q.front().first;
            int j=q.front().second;
            q.pop();
            for(int k=0; k<4; k++) {
                int ci=DR[k]+i;
                int cj=DC[k]+j;
                if(!valid_index(ci, cj, grid))
                    continue;
                if(grid[ci][cj]=='1') {
                    q.push({ci, cj});
                    grid[ci][cj]='0';
                }
            }
        }
    }

    int numIslands(vector<vector<char>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int no_of_islands=0;
        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                if(grid[i][j]=='1') {
                    no_of_islands++;
                    bfs(i, j, grid);
                }
            }
        }
        return no_of_islands;
    }
};
```

**Method 3: Union Find**
Time Complexity : O(n x m)
Space Complexity: O(n x m)

```
class Solution {
public:
    int no_of_islands=0;
    int DC[4]={0, 1, 0, -1};
    int DR[4]={1, 0, -1, 0};

    int find_parent(int a, vector<int> &parent) {
        if(parent[a]==a) return a;
        return parent[a]=find_parent(parent[a], parent);
    }

    void union_(int a, int b, vector<int> &parent) {
        int pa=find_parent(a, parent);
        int pb=find_parent(b, parent);
        if(pa==pb) return;
        parent[pa]=pb;
        no_of_islands--;
    }

    int numIslands(vector<vector<char>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        vector<int> parent(n*m);        
        for(int i=0; i<n*m; i++)
            parent[i]=i;
        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                if(grid[i][j]=='1') {
                    no_of_islands++;
                    for(int k=0; k<4; k++) {
                        int ci=DR[k]+i;
                        int cj=DC[k]+j;
                        if(ci<0 || cj<0 || ci>=n || cj>=m)
                            continue;
                        if(grid[ci][cj]=='1') {
                            union_((i*m)+j, (ci*m)+cj, parent);
                        }
                    }
                }
            }
        }
        return no_of_islands;
    }
};
```

