    //time: o(row * column)
    //space: o(row)
    int numIslands(vector<vector<char>>& grid) {
        if(grid.empty())
            return 0;

        int count_of_islands = 0;
        for(int row = 0; row < grid.size(); ++row) {
            for(int col = 0; col < grid[0].size(); ++col) {
                if(grid[row][col] == '1') {
                    ++count_of_islands;
                    dfs(grid, row, col);//use dfs to mark every grid = 2 which belongs to this island.
