class Solution {
public:
    int numIslands(vector<vector<char>>& grid) {
        if(grid.empty())    return 0;
        int m = grid.size(), n = grid[0].size(), cnt = 0;
        vector<vector<bool>> visited(m, vector<bool>(n, 0));

        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                if(!visited[i][j] && grid[i][j] == '1') {
                    dfs(grid, i, j, visited);
                    cnt++;
                }
            }
        }
        return cnt;
    }

    void dfs(vector<vector<char>> &grid, int i, int j, vector<vector<bool>> &visited) {
        if(i < 0 || i >= grid.size() || j < 0 || j >= grid[0].size() || visited[i][j] || grid[i][j] == '0')     return;

        visited[i][j] = 1;
        dfs(grid, i-1, j, visited);
        dfs(grid, i, j+1, visited);
        dfs(grid, i+1, j, visited);
        dfs(grid, i, j-1, visited);
    }
};

/*
BFS
Runtime - 28 ms, Memory - 10.7 MB
*/
class Solution {
public:
    int numIslands(vector<vector<char>>& grid) {
        if(grid.empty())    return 0;
        int m = grid.size(), n = grid[0].size(), cnt = 0;
        vector<vector<bool>> visited(m, vector<bool>(n, 0));

        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                if(!visited[i][j] && grid[i][j] == '1') {
                    cnt++;
                    bfs(grid, i, j, visited);
                }
            }
        }
        return cnt;
    }

    void bfs(vector<vector<char>> &grid, int i, int j, vector<vector<bool>> &visited) {
        // lambda to check whether current cell is inside grid
        auto inside = [&](const pair<int, int> &p) -> bool {
            return p.first >= 0 && p.first < grid.size() && p.second >= 0 && p.second < grid[0].size();
        };

        // adding any pair to a coordinate gives a neighboring cell (up, right, down, left)
        vector<pair<int, int>> direction = {{-1, 0}, {0, 1}, {1, 0}, {0, -1}};

        queue<pair<int, int>> q;
        q.push({i, j});
        while(!q.empty()) {
            auto curr_cell = q.front();
            q.pop();
            visited[curr_cell.first][curr_cell.second] = 1;

            // for all neighboring cells of current cell
            for(auto dir: direction) {
                int new_i = (curr_cell.first + dir.first), new_j = (curr_cell.second + dir.second);
                auto new_cell = make_pair(new_i, new_j);
                // if neighboring cell lies inside grid and not visited already and has island part (i.e '1') then add it to queue
                if(inside(new_cell) && !visited[new_i][new_j] && grid[new_i][new_j] == '1') {
                    visited[new_i][new_j] = 1;
                    q.push(new_cell);
                }
            }
        }
    }
};

