public int NumIslands(char[][] grid) 
    {
        if(grid == null)
            return 0;

        var rows = grid.Length;
        var cols = grid[0].Length;
        var islands = 0;

        for(int row=0; row<rows; row++)
        {
            for(int col=0; col<cols; col++)
            {
                if(grid[row][col] == '1')
                {
					//want to apply DFS, replace following call with CheckLandBelongsToIsland**DFS**
                    CheckLandBelongsToIslandBFS(grid, row, col); 
                    islands +=1;   
                }
            }
        }

        return islands;
    }

	//BFS
    public void CheckLandBelongsToIslandBFS(char[][] grid, int row, int col)
    {
        var queue = new Queue<int[]>();
        queue.Enqueue(new int[] { row, col});


        while(queue.Count != 0)
        {
            var item = queue.Dequeue();
            row = item[0];
            col = item[1];

            if(row < 0 || row >= grid.Length || col < 0 || col >= grid[0].Length || grid[row][col] != '1')
            {
                continue;
            }

            grid[row][col] = '#'; //Mark as Visited

            queue.Enqueue(new int[] { row - 1, col}); //UP
            queue.Enqueue(new int[] { row + 1, col}); //Down
            queue.Enqueue(new int[] { row, col + 1}); //Right
            queue.Enqueue(new int[] { row, col - 1}); //Left
        }
    }

	//DFS
    public void CheckLandBelongsToIslandDFS(char[][] grid, int row, int col)
    {
        if(row<0 || row >= grid.Length || col < 0 || col >= grid[0].Length || grid[row][col] != '1')
