    int lengthOfLIS(vector<int>& nums, int i = 0, int prev = INT_MIN) {
        if(i == size(nums)) return 0;
        return max(lengthOfLIS(nums, i + 1, prev), (nums[i] > prev) + lengthOfLIS(nums, i + 1, max(nums[i], prev)));
