    def lengthOfLIS(self, nums: List[int]) -> int:
        if not nums:
            return 0
        n, res = len(nums), 1
        for i in range(n - 1):
            cur, LIS = nums[i], 1
            for j in range(i + 1, n):
                if nums[j] > cur:
                    LIS += 1
                    cur = nums[j]
            res = max(res, LIS)
