//MEMOIZATION 
class Solution {
public:
    int memo[2501];
    int lis_ending_here(vector<int>& arr, int curr){
        if(curr == 0)
            return 1;
        if(memo[curr]!=-1)
            return memo[curr];
        int ans = 1;
        for(int i = curr-1;i>=0;i--)
            if(arr[i] < arr[curr])
                ans = max(ans, 1 + lis_ending_here(arr, i));
        return memo[curr]=ans;
    }
    int lengthOfLIS(vector<int>& nums) {
        memset(memo,-1,sizeof(memo));
        int max_ans = 1;
        for(int i = 0 ;i<nums.size();i++)
            max_ans = max(max_ans, lis_ending_here(nums, i));
        return max_ans;
    }
};
