int findLis(vector<int>& nums,int n,int currIndex,int currEle){

    if(currIndex >= n){
        return 1;
    }

    int nextEle = nums[currIndex];

    if(nextEle <= currEle){
        return findLis(nums,n,currIndex+1,currEle);
    }

    int opt1 = 1 + findLis(nums,n,currIndex+1,nextEle);
    int opt2 = findLis(nums,n,currIndex+1,currEle);
    return max(opt1,opt2);
}

class Solution {
public:
    int lengthOfLIS(vector<int>& nums) {

        //first we will try to solve this problem by using recursion only
        //so this is just recursion approach

        int n = nums.size();
        if(n == 0 || n == 1)  return n;

        int maxLis = INT_MIN;

        for(int i=0;i<n;i++){
            //single element always forms a lis
            //you have to start dearching from the i+1 index in the nums array
            int currLenOfLis = findLis(nums,n,i+1,nums[i]);

            if(currLenOfLis > maxLis){
                maxLis = currLenOfLis;
            }

        }

        return maxLis;
    }
};
