class Solution {
public:
    int lengthOfLIS(vector<int>& nums) {
        //now we will try bottom-up approach
        int n = nums.size();
        if(n == 0 || n == 1)    return n;
        vector<int> dp(n,1);
        int maxLen = 1;

        for(int i=0;i<n;i++){

            for(int j=0;j<i;j++){
                if(nums[j] < nums[i]){
                    dp[i] = max(dp[i],dp[j]+1);
