***Binary Search***
```
int findUsingBinarySearch(vector<int>& temp,int start,int end,int currEle){

    while(start <= end){

        int middle = (start+end)/2;

        if(temp[middle] > currEle){
            if(middle == 0) return middle;
            if(temp[middle-1] < currEle){
                return middle;
            }
        }

        if(temp[middle] < currEle){
            start = middle+1;
        }else{
            end = middle-1;
        }

    }
    return -1;
}

class Solution {
public:
    int lengthOfLIS(vector<int>& nums) {

        //we will be using binary search to find out the lis
        //time complexity for this code will be O(nlogn)

        int n = nums.size();
        if(n  == 0 || n == 1)   return n;

        vector<int> temp;

        int maxLen = 1;

        for(int i=0;i<n;i++){
            int currEle = nums[i];
            int len = temp.size();

            if(temp.size() == 0){
                temp.push_back(currEle);
            }else{
                if(temp[len-1] < currEle){
                    temp.push_back(currEle);
                }else{
                    //find and replace using binary search
                    int index = findUsingBinarySearch(temp,0,len-1,currEle);
                    if(index != -1)
                        temp[index] = currEle;
                }
            }

            if(temp.size() > maxLen){
                maxLen = temp.size();
            }

        }

        return maxLen;
    }
};
```

