class Solution:
    from math import inf
    def lengthOfLIS(self, nums: List[int]) -> int:
        memo = {}
        def length_of_longest_sequence(prev_largest, curr_position):
            if curr_position == len(nums):
                return 0
            if (prev_largest, curr_position) in memo:
                return memo[(prev_largest, curr_position)]
            take = -inf
            notake = length_of_longest_sequence(prev_largest, curr_position + 1)
            c_val = nums[curr_position]
            if c_val > prev_largest:
                take = length_of_longest_sequence(c_val, curr_position + 1) + 1

            result = max(take, notake)
            memo[(prev_largest, curr_position)] = result
            return result

        return length_of_longest_sequence(-inf, 0)
