class Solution {
public:
    int lengthOfLIS(vector<int>& nums) {
        int n=nums.size();
        int* output = new int[n];
        output[0]=1;

        for(int i=1;i<n;i++)
        {
            output[i]=1;

            for(int j=i-1;j>=0;j--)
            {
                if(nums[j]>=nums[i])
                {
                    continue;
                }

                int posbAns= output[j] + 1;
                if(posbAns > output[i])
                   output[i]=posbAns;

              }
        }

        int best=0;
        for(int i=0;i<n;i++)
        {
            if(best < output[i])
                best=output[i];
        }
        delete [] output;
        return best;
    }
};
