	public int lengthOfLIS(int[] n) {
        ArrayList<Integer> al = new ArrayList<Integer>();
        al.add(n[0]);
        for(int i=1;i<n.length;i++)
            if(al.get(al.size()-1) < n[i]) al.add(n[i]);
            else {
                int ind = Collections.binarySearch(al, n[i]);
                if(ind<0)al.set(-(ind+1), n[i]);
            }
        return al.size();
    }
