class Solution {
    public int lengthOfLIS(int[] nums) {
        return solve(nums, Integer.MIN_VALUE, 0);
    }

    public int solve(int[] nums, int prev, int curr){
        if(curr==nums.length){
            return 0;
        }

        if(nums[curr]>prev){
           return Math.max(1+solve(nums, nums[curr], curr+1),
                          solve(nums, prev, curr+1));
        }
         else{
             return solve(nums, prev, curr+1);
         }
    }
}
