class Solution {
    public int lengthOfLIS(int[] nums) {
        TreeSet<Integer> set = new TreeSet<>();
        for (int i : nums) {
            Integer a = set.ceiling(i);
            if (a != null) set.remove(a);
            set.add(i);
        }
        return set.size();
    }
}
